import { SaveOutlined } from "@mui/icons-material";
import { Box, Button, Paper, TextField, Typography } from "@mui/material";
import React, { useState } from "react";
import { string } from "yup";

type ButtonProps = {
  update: (value: number) => void;
};

const save = (e: React.MouseEvent, formData: any) => {
  console.log(formData);
};

interface formDataInterface {
  firstName: string;
  lastName: string;
  email: string;
}

const CreateContact = ({ update }: ButtonProps) => {

  const [formData, setFormData] = useState<formDataInterface>({firstName : string, lastName : string, email : string});

  return (
    <Box
      sx={{
        width: "100%",
        height: "90vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        alignContent: "center",
        border: 1,
        borderColor: "lightgrey",
        overflowX: "hidden",
      }}
    >
      <Box
        sx={{
          width: "70%",
          height: "80vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          alignContent: "center",
          border: 1,
          borderColor: "lightgrey",
        }}
      >
        <Paper elevation={3}>
          <form>
            <Box
              component={"form"}
              sx={{ width: "80%" }}
              onSubmit={(e: MouseEvent, formData: any) => save(e, formData)}
            >
              <Box
                sx={{
                  display: "flex",
                  direction: "row",
                }}
              >
                <TextField
                  name="firstName"
                  label={"FirstName"}
                  variant={"outlined"}
                  sx={{ width: "100%", mr: 2 }}
                  type={"text"}
                />
                <TextField
                  name="lastName"
                  label={"LastName"}
                  variant={"outlined"}
                  sx={{ width: "100%" }}
                  type={"text"}
                />
              </Box>

              <TextField
                name="email"
                label={"Email"}
                variant={"outlined"}
                sx={{ width: "100%", mt: 2 }}
                type={"email"}
              />

              <Button
                type="submit"
                variant="outlined"
                endIcon={<SaveOutlined />}
                sx={{ width: "100%", mt: 2 }}
              >
                <Typography align="center">Save</Typography>
              </Button>
            </Box>
          </form>
        </Paper>
      </Box>
    </Box>
  );
};

export default CreateContact;
